using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\nclass Program\n{\n    static void Main()\n    {\n        string passwordToEncrypt = "YourSecretPassword123!";\n        \n        // Encrypting the data\n        byte[] encryptedData = ProtectedData.Protect(\n            Encoding.UTF8.GetBytes(passwordToEncrypt),\n            null,\n            DataProtectionScope.CurrentUser);\n        \n        Console.WriteLine("Encrypted: " + Convert.ToBase64String(encryptedData));\n        \n        // Decrypting the data\n        byte[] decryptedData = ProtectedData.Unprotect(\n            encryptedData,\n            null,\n            DataProtectionScope.CurrentUser);\n        \n        string decryptedPassword = Encoding.UTF8.GetString(decryptedData);\n        Console.WriteLine("Decrypted: " + decryptedPassword);\n    }\n}