using System.Net;\nusing System.Net.Mail;\n\npublic static void SendEmail(string fromAddress, string toAddress, string subject, string body)\n{\n    var smtpClient = new SmtpClient("smtp.yourserver.com")\n    {\n        Credentials = new NetworkCredential("user@yourdomain.com", "password"),\n        EnableSsl = true,\n        Port = 587\n    };\n\n    using (var mailMessage = new MailMessage(fromAddress, toAddress, subject, body))\n    {\n        smtpClient.Send(mailMessage);\n    }\n}