using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string path = @"C:\YourDirectoryPath"; // Replace with your path\n        CheckForMountedFolders(path);\n    }\n\n    static void CheckForMountedFolders(string directoryPath)\n    {\n        if (!Directory.Exists(directoryPath))\n            return;\n\n        var dirs = Directory.GetDirectories(directoryPath);\n\n        foreach (var dir in dirs)\n        {\n            try\n            {\n                DirectoryInfo info = new DirectoryInfo(dir);\n                FileInfoAttributes attributes = info.Attributes;\n\n                // Check if the directory is a reparse point (junction or symbolic link)\n                if ((attributes & FileAttributes.ReparsePoint) == FileAttributes.ReparsePoint)\n                {\n                    Console.WriteLine($"{dir} is a mounted folder.");\n                }\n\n                // Recursively check subdirectories\n                CheckForMountedFolders(dir);\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"Error accessing {dir}: {ex.Message}");\n            }\n        }\n    }\n}