using System;\nusing System.Windows.Forms;\n\npublic class ScriptInterface {\n    public void AddFavorite(string url) {\n        // Implement logic securely, sanitize inputs if necessary\n        MessageBox.Show($"Adding favorite: {url}");\n    }\n}\n\npublic partial class Form1 : Form {\n    private ScriptInterface scriptInterface;\n\n    public Form1() {\n        InitializeComponent();\n        InitializeWebBrowser();\n    }\n\n    private void InitializeWebBrowser() {\n        scriptInterface = new ScriptInterface();\n        webBrowser1.ObjectForScripting = scriptInterface;\n        webBrowser1.Navigate("about:blank");\n        webBrowser1.Document.Write("<html><body>Loaded</body></html>");\n        webBrowser1.Document.InvokeScript("eval", new object[] { "window.external.AddFavorite('http://www.google.com');" });\n    }\n}