using (var context = new YourDbContext())\n{\n    int productId = yourProductId;\n    string newName = "NewName";\n    int customerId = yourCustomerId;\n\n    // Constructing an update query manually that includes CustomerId in the WHERE clause\n    var sqlQuery = $"UPDATE Product SET Name = {context.Database.GenerateParameterName("newName")} " +\n                   $"WHERE Id = {context.Database.GenerateParameterName("productId")} " +\n                   $"AND CustomerId = {context.Database.GenerateParameterName("customerId")}";\n    \n    context.Database.ExecuteSqlRaw(sqlQuery, \n                                  new SqlParameter("@newName", newName),\n                                  new SqlParameter("@productId", productId),\n                                  new SqlParameter("@customerId", customerId));\n\n    // Save changes if there are other entities to be saved\n    // context.SaveChanges();\n}