using DevExpress.XtraGrid;\nusing DevExpress.XtraGrid.Views.Grid;\n\npublic void InitializeGridControl(AdvBandedGridView advBandedGridView)\n{\n    // Enable grouping\n    advBandedGridView.BandsPanel.Visible = true; // Show the group panel at the top\n\n    // Configure columns and enable editing\n    GridColumn columnCategory = new GridColumn();\n    columnCategory.FieldName = "Category";\n    columnCategory.OptionsColumn.AllowGroup = DevExpress.Utils.DefaultBoolean.True;\n    advBandedGridView.Columns.Add(columnCategory);\n\n    GridColumn columnName = new GridColumn();\n    columnName.FieldName = "Name";\n    columnName.OptionsColumn.AllowEdit = true; // Enable editing\n    advBandedGridView.Columns.Add(columnName);\n}