protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Get the WONumber from the query string\n        string wonumber = Request.QueryString["WONumber"];\n\n        // Set the href attribute dynamically\n        // Assuming you have an anchor tag with ID "LinkToPage3" in your aspx file\n        LinkToPage3.HRef = "Page3.aspx?WONumber=" + Server.UrlEncode(wonumber);\n    }\n}