// Example connection string for SQL Server in C# (ASP.NET)\nstring connectionString = "Server=myServerAddress;Database=myDataBase;User Id=myUsername;Password=myPassword;";\nusing (SqlConnection con = new SqlConnection(connectionString))\n{\n    SqlCommand cmd = new SqlCommand("SELECT * FROM Users", con);\n    con.Open();\n    SqlDataReader dr = cmd.ExecuteReader();\n    // Process your data here\n}\n\n// Example connection string for Access in C# (ASP.NET)\nstring accessConnectionString = "Provider=Microsoft.Jet.OLEDB.4.0;Data Source=C:\\path\\to\\mydatabase.mdb;";\nusing (OleDbConnection con = new OleDbConnection(accessConnectionString))\n{\n    OleDbCommand cmd = new OleDbCommand("SELECT * FROM Users", con);\n    con.Open();\n    OleDbDataReader dr = cmd.ExecuteReader();\n    // Process your data here\n}