using System;\nusing System.Windows.Forms;\n\npublic class HtmlHelpViewer : Form\n{\n    private WebBrowser webBrowser;\n\n    public HtmlHelpViewer()\n    {\n        // Initialize the WebBrowser control.\n        webBrowser = new WebBrowser();\n        webBrowser.Dock = DockStyle.Fill;  // Make it fill the entire form.\n\n        // Load an HTML document from a local path or remote URL.\n        this.Load += (s, e) => LoadContent();\n\n        Controls.Add(webBrowser);\n    }\n\n    private void LoadContent()\n    {\n        // Local file\n        webBrowser.Url = new Uri("file:///C:/path/to/help.html");\n        \n        // Or use an online URL\n        //webBrowser.Url = new Uri("https://www.example.com/help");\n    }\n    \n    [STAThread]\n    public static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n        Application.Run(new HtmlHelpViewer());\n    }\n}