[HttpPost]\npublic ActionResult Add(Comment comment)\n{\n    if (ModelState.IsValid)\n    {\n        // Save your comment to the database and retrieve updated list\n        var comments = GetComments(); // Assume this method fetches all comments from db or cache.\n        return PartialView("_CommentList", comments);\n    }\n    else\n    {\n        return new HttpStatusCodeResult(HttpStatusCode.BadRequest);\n    }\n}\n\npublic ActionResult _CommentList()\n{\n    var comments = GetComments(); // Fetch comments from the database.\n    return PartialView(comments);\n}