using System;\nusing System.Collections.Generic;\nusing System.Diagnostics;\nusing System.Windows.Input;\n\npublic class KeyResolver\n{\n    private static readonly Dictionary<char, Key> charToKeyMap = new Dictionary<char, Key>\n    {\n        // Alphanumeric keys\n        {'0', Key.D0}, {'1', Key.D1}, {'2', Key.D2}, {'3', Key.D3},\n        {'4', Key.D4}, {'5', Key.D5}, {'6', Key.D6}, {'7', Key.D7},\n        {'8', Key.D8}, {'9', Key.D9}, {'A', Key.A}, {'B', Key.B},\n        {'C', Key.C}, {'D', Key.D}, {'E', Key.E}, {'F', Key.F},\n        {'G', Key.G}, {'H', Key.H}, {'I', Key.I}, {'J', Key.J},\n        {'K', Key.K}, {'L', Key.L}, {'M', Key.M}, {'N', Key.N},\n        {'O', Key.O}, {'P', Key.P}, {'Q', Key.Q}, {'R', Key.R},\n        {'S', Key.S}, {'T', Key.T}, {'U', Key.U}, {'V', Key.V},\n        {'W', Key.W}, {'X', Key.X}, {'Y', Key.Y}, {'Z', Key.Z},\n\n        // Punctuation keys\n        {'.', Key.OemPeriod}, {',', Key.OemComma}, {';', Key.OemSemicolon},\n        {'\'', Key.OemQuotes}, {'/', Key.Divide}, {'\\', Key.OemBackslash},\n        {'[', Key.OemOpenBrackets}, {']', Key.OemCloseBrackets},\n\n        // Other keys\n        {' ', Key.Space}, {'`', Key.Oem3}\n    };\n\n    public static Key ResolveKey(char charToResolve)\n    {\n        // Convert character to uppercase since the dictionary uses uppercase letters for A-Z\n        char upperChar = Char.ToUpper(charToResolve);\n\n        if (charToKeyMap.TryGetValue(upperChar, out Key key))\n        {\n            return key;\n        }\n        \n        throw new ArgumentException($"No corresponding Key found for character: {charToResolve}");\n    }\n\n    public static void Main(string[] args)\n    {\n        Debug.Assert(KeyResolver.ResolveKey('A') == Key.A);\n        Debug.Assert(KeyResolver.ResolveKey('a') == Key.A); // Case-insensitive\n        Debug.Assert(KeyResolver.ResolveKey('.') == Key.OemPeriod);\n        \n        try\n        {\n            KeyResolver.ResolveKey('?'); // Should throw an exception\n        }\n        catch (ArgumentException ex)\n        {\n            Console.WriteLine(ex.Message);\n        }\n    }\n}