// Example of using reflection to get the type information of a COM object\n   var pic = range.Worksheet.Pictures.Insert(tmpFile);\n   Type comObjectType = pic.GetType();\n   \n   // Get all properties\n   PropertyInfo[] properties = comObjectType.GetProperties(BindingFlags.Public | BindingFlags.Instance);\n   foreach (PropertyInfo prop in properties)\n   {\n       Console.WriteLine(prop.Name);\n   }\n\n   // Get all methods\n   MethodInfo[] methods = comObjectType.GetMethods(BindingFlags.Public | BindingFlags.Instance);\n   foreach (MethodInfo method in methods)\n   {\n       Console.WriteLine(method.Name);\n   }