[Test]\npublic void GetUserById_ShouldReturnUser_WhenUserIdExists()\n{\n    // Arrange\n    var mockContext = new Mock<UserDataContext>();\n    var users = new List<User>\n    {\n        new User { Id = 1, Name = "Test User" }\n    }.AsQueryable();\n\n    mockContext.Setup(c => c.Users).Returns(users);\n    var userRepository = new UserRepository(mockContext.Object);\n\n    // Act\n    var user = userRepository.GetUserById(1);\n\n    // Assert\n    Assert.IsNotNull(user);\n    Assert.AreEqual(1, user.Id);\n}