public ActionResult Register(UserRegistrationModel model)\n{\n    if (ModelState.IsValid)\n    {\n        // Create user account logic here\n\n        // Send verification email\n        var verificationLink = Url.Action("VerifyEmail", "Account", new { token = "your-generated-token" }, Request.Url.Scheme);\n        var message = new MailMessage("from@example.com", model.EmailAddress, \n            "Verify Your Email Address",\n            $"Please click the link below to verify your email address: <a href='{verificationLink}'>Verify Email</a>");\n        \n        using (var client = new SmtpClient())\n        {\n            await client.SendMailAsync(message);\n        }\n\n        // Mark account as unverified\n        MarkAccountAsUnverified(model.EmailAddress);\n\n        return View("ThankYou");\n    }\n    return View();\n}