private void LoadXmlDataByGenre()\n{\n    DataSet dataSet = new DataSet("Songs");\n    dataSet.ReadXml(@"C:\path\to\your\Songs.xml");\n\n    // Assuming you have the ListView columns set as Name, Artist, Genre\n\n    var dataTable = dataSet.Tables["Song"];\n    var groupedSongs = dataTable.AsEnumerable()\n        .GroupBy(row => row.Field<string>("Genre"))\n        .ToList();\n\n    foreach (var group in groupedSongs)\n    {\n        string groupName = group.Key;\n        // Insert group header\n        ListViewItem.groupItem = new ListViewItem("Genre: " + groupName) { UseItemStyleForSubItems = false };\n        listViewSongs.Items.Add(groupItem);\n\n        foreach (DataRowView row in group)\n        {\n            var item = new ListViewItem(row["Name"].ToString());\n            item.SubItems.Add(row["Artist"].ToString());\n            item.SubItems.Add(row["Genre"].ToString());\n            listViewSongs.Items.Add(item);\n        }\n    }\n\n    listViewSongs.Sort();\n}