using System;\nusing System.Collections.Generic;\nusing System.Data;\nusing Oracle.ManagedDataAccess.Client;\n\npublic partial class _Default : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        if (!IsPostBack)\n            return;\n        \n        var parameters = new Dictionary<string, object>\n        {\n            { "ListBox1", ListBox1.SelectedValue },\n            { "DropDownList1", DropDownList1.SelectedValue }\n            // Add other controls as needed\n        };\n\n        QueryBuilder queryBuilder = new QueryBuilder();\n        string sql = queryBuilder.BuildQuery(parameters);\n\n        using (OracleConnection conn = new OracleConnection("YOUR_CONNECTION_STRING"))\n        {\n            using (OracleCommand cmd = new OracleCommand(sql, conn))\n            {\n                if (!string.IsNullOrEmpty(ListBox1.SelectedValue))\n                    cmd.Parameters.Add(new OracleParameter("param1", ListBox1.SelectedValue));\n\n                if (!string.IsNullOrEmpty(DropDownList1.SelectedValue))\n                    cmd.Parameters.Add(new OracleParameter("param2", DropDownList1.SelectedValue));\n\n                // Add other parameters as needed\n\n                try\n                {\n                    conn.Open();\n                    DataTable dt = new DataTable();\n\n                    using (OracleDataAdapter da = new OracleDataAdapter(cmd))\n                        da.Fill(dt);\n\n                    DataGrid1.DataSource = dt;\n                    DataGrid1.DataBind();\n                }\n                catch (Exception ex)\n                {\n                    // Handle exception\n                }\n            }\n        }\n    }\n}