using System;\nusing System.Data;\nusing Oracle.ManagedDataAccess.Client;\n\npublic class QueryBuilder\n{\n    public string BuildQuery(IDictionary<string, object> parameters)\n    {\n        var sql = "SELECT * FROM YOUR_TABLE WHERE 1=1"; // Start with a true condition\n\n        if (parameters.ContainsKey("ListBox1") && !string.IsNullOrEmpty(parameters["ListBox1"].ToString()))\n        {\n            sql += " AND COLUMN_NAME1 = :param1";\n        }\n\n        if (parameters.ContainsKey("DropDownList1") && !string.IsNullOrEmpty(parameters["DropDownList1"].ToString()))\n        {\n            sql += " AND COLUMN_NAME2 = :param2";\n        }\n\n        // Continue for other controls\n\n        return sql;\n    }\n}