using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class RSAEncryptionExample\n{\n    public static void Main()\n    {\n        // Generate RSA key pair in memory\n        using (var rsa = RSA.Create())\n        {\n            // Export the public and private keys if needed\n            RSAParameters publicKey = rsa.ExportParameters(false);\n            RSAParameters privateKey = rsa.ExportParameters(true);\n\n            // Example of encrypting data\n            string original = "Hello, World!";\n            byte[] originalData = Encoding.UTF8.GetBytes(original);\n\n            byte[] encryptedData;\n            using (var encryptor = RSA.Create())\n            {\n                encryptor.ImportParameters(publicKey);\n                encryptedData = encryptor.Encrypt(originalData, RSAEncryptionPadding.OaepSHA256);\n            }\n\n            byte[] decryptedData;\n            using (var decryptor = RSA.Create())\n            {\n                decryptor.ImportParameters(privateKey);\n                decryptedData = decryptor.Decrypt(encryptedData, RSAEncryptionPadding.OaepSHA256);\n            }\n\n            string decrypted = Encoding.UTF8.GetString(decryptedData);\n\n            Console.WriteLine($"Original: {original}");\n            Console.WriteLine($"Decrypted: {decrypted}");\n        }\n    }\n}