using System;\nusing System.IO;\n\npublic string FindImageFile(string directoryPath, string fileNameWithoutExtension)\n{\n    // Define valid image extensions\n    string[] imageExtensions = { ".jpg", ".jpeg", ".png", ".gif", ".bmp" }; // Add more if needed\n\n    foreach (var ext in imageExtensions)\n    {\n        // Construct the full file path with current extension\n        string filePath = Path.Combine(directoryPath, fileNameWithoutExtension + ext);\n\n        // Check if file exists\n        if (File.Exists(filePath))\n        {\n            return filePath; // Return the full path of the found image\n        }\n    }\n\n    return null; // Return null if no image file is found with any known extension\n}