using System;\nusing System.IO;\nusing System.Net;\n\npublic class WebPageDownloader\n{\n    public string DownloadHtml(string url)\n    {\n        // Create an HttpWebRequest object\n        HttpWebRequest request = (HttpWebRequest)WebRequest.Create(url);\n        \n        try\n        {\n            // Get the response from the server\n            using (HttpWebResponse response = (HttpWebResponse)request.GetResponse())\n            {\n                if (response.StatusCode == HttpStatusCode.OK)\n                {\n                    // Read the data stream returned by the server\n                    string html;\n                    using (Stream answerStream = response.GetResponseStream())\n                    {\n                        using (StreamReader reader = new StreamReader(answerStream))\n                        {\n                            html = reader.ReadToEnd();\n                        }\n                    }\n                    return html;\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error: " + ex.Message);\n        }\n\n        return null; // Return null if the download fails\n    }\n}