using System;\nusing CsvParsers;\n\nclass Program\n{\n    static void Main()\n    {\n        try\n        {\n            string filePath = @"C:\path\to\your\file.csv";\n            CSVParser parser = new CSVParser();\n            DataTable dataTable = parser.ParseCsv(filePath);\n\n            foreach (DataRow row in dataTable.Rows)\n            {\n                Console.WriteLine(string.Join(", ", row.ItemArray));\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error: " + ex.Message);\n        }\n    }\n}