List<Int64> ListOfPrimeFactors = new List<Int64>();\nInt64 Number = 600851475143;\nInt64 DividingNumber = 2;\n\n// Check for number of 2s that divide Number\nwhile (Number % DividingNumber == 0)\n{\n    ListOfPrimeFactors.Add(DividingNumber);\n    Number /= DividingNumber;\n}\n\n// n must be odd at this point so we can skip even numbers\nDividingNumber = 3;\nwhile (DividingNumber <= Math.Sqrt(Number))\n{\n    while (Number % DividingNumber == 0)\n    {\n        ListOfPrimeFactors.Add(DividingNumber);\n        Number /= DividingNumber;\n    }\n    DividingNumber += 2;\n}\n\n// This condition is to check if Number is a prime number greater than 2\nif (Number > 2)\n{\n    ListOfPrimeFactors.Add(Number);\n}\n\nlistBox1.DataSource = ListOfPrimeFactors;