using System;\nusing System.Diagnostics;\n\nclass MemoryUsageDemo\n{\n    static void Main()\n    {\n        while (true)\n        {\n            // Get the total allocated memory.\n            long usedMemory = GC.GetTotalMemory(false);\n            Console.WriteLine($"Memory Used: {usedMemory} bytes");\n\n            // Simulate some processing and wait for 1 second.\n            System.Threading.Thread.Sleep(1000);\n        }\n    }\n}