using System;\nusing System.Net;\n\npublic class DomainChecker\n{\n    public static void EnsureCorrectDomain()\n    {\n        string allowedDomain = "youralloweddomain.com";\n        string hostName = Dns.GetHostName();\n        string hostEntryIp = Dns.GetHostAddresses(hostName)[0].ToString();\n\n        if (!hostName.EndsWith(allowedDomain) && !IsLocalhost(hostEntryIp))\n            throw new UnauthorizedAccessException("This software is licensed to run only on " + allowedDomain);\n    }\n\n    private static bool IsLocalhost(string ipAddress)\n    {\n        return ipAddress == "127.0.0.1" || ipAddress == "::1";\n    }\n}