using System;\nusing System.Runtime.InteropServices;\n\npublic class ComInteropExample\n{\n    [ComImport]\n    [Guid("0002DF01-0000-0000-C000-000000000046")]\n    public class InternetExplorer\n    {\n    }\n\n    [ComImport, DefaultMember("Name"), Guid("D30C1661-CDAF-11D0-8A3E-00C04FC9E26E"), InterfaceType(ComInterfaceType.InterfaceIsIDispatch), SuppressUnmanagedCodeSecurity]\n    public interface IWebBrowser2\n    {\n        [DispId(500)]\n        void Navigate2([In] ref object URL, [In] ref object Flags, [In] ref object TargetFrameName, [In] ref object PostData, [In] ref object Headers);\n\n        [DispId(0xcb)]\n        object Document { get; }\n    }\n\n    public static void SpawnIEWithSource(String szHtml)\n    {\n        IWebBrowser2 ie = (IWebBrowser2)new InternetExplorer();\n\n        // Navigate to about:blank to initialize the browser\n        object o = System.Reflection.Missing.Value;\n        String url = "about:blank";\n        ie.Navigate2(ref url, ref o, ref o, ref o, ref o);\n\n        // Wait for the document to be ready\n        while (ie.Document == null)\n        {\n            System.Threading.Thread.Sleep(100);\n        }\n\n        // Get the document object\n        object webDocument = ie.Document;\n\n        // Write HTML content to the document\n        Type documentType = webDocument.GetType();\n        documentType.InvokeMember("write", System.Reflection.BindingFlags.InvokeMethod, null, webDocument, new object[] { szHtml });\n\n        // Close the document stream\n        documentType.InvokeMember("close", System.Reflection.BindingFlags.InvokeMethod, null, webDocument, null);\n\n        // Make IE visible\n        ie.GetType().InvokeMember("Visible", System.Reflection.BindingFlags.SetProperty, null, ie, new object[] { true });\n    }\n}