protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        BindMasterGrid();\n    }\n}\n\nprivate void BindMasterGrid()\n{\n    // Fetch master data from database and bind to MasterGrid\n    MasterGrid.DataSource = GetDataForMaster(); // Implement this method to fetch data\n    MasterGrid.DataBind();\n}\n\nprotected void MasterGrid_RowDataBound(object sender, GridViewRowEventArgs e)\n{\n    if (e.Row.RowType == DataControlRowType.DataRow)\n    {\n        int masterId = Convert.ToInt32(DataBinder.Eval(e.Row.DataItem, "MasterID")); // Assuming there is a unique identifier for each master row\n\n        var detailPanel = new Panel() { CssClass = "detailpanel" };\n        var detailGrid = new GridView();\n        \n        BindDetailGrid(detailGrid, masterId);\n\n        detailPanel.Controls.Add(detailGrid);\n        e.Row.Cells[0].Controls.Add(detailPanel);\n    }\n}\n\nprivate void BindDetailGrid(GridView detailGrid, int masterId)\n{\n    // Fetch detail data for the given master ID from database and bind to DetailGrid\n    detailGrid.DataSource = GetDataForDetails(masterId); // Implement this method to fetch data\n    detailGrid.DataBind();\n}