using System;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "YourConnectionString";\n        string htmlContent = "<b>demo</b>"; // This should be sanitized\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            string query = "INSERT INTO YourTable (HtmlColumn) VALUES (@HtmlContent)";\n\n            using (SqlCommand command = new SqlCommand(query, connection))\n            {\n                command.Parameters.AddWithValue("@HtmlContent", htmlContent);\n\n                try\n                {\n                    connection.Open();\n                    int rowsWritten = command.ExecuteNonQuery();\n                    Console.WriteLine($"{rowsWritten} row(s) inserted.");\n                }\n                catch (Exception ex)\n                {\n                    Console.WriteLine("An error occurred: " + ex.Message);\n                }\n            }\n        }\n    }\n}