protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        PopulateExecutableDropdown();\n    }\n}\n\nprivate void PopulateExecutableDropdown()\n{\n    string directoryPath = Server.MapPath("~/Executables"); // Adjust the path as necessary\n    DropDownList1.Items.Clear();\n\n    try\n    {\n        // Get all executable files in the specified directory\n        foreach (string file in Directory.GetFiles(directoryPath, "*.exe"))\n        {\n            // Add each file to the dropdown\n            ddlExecutableFiles.Items.Add(new ListItem(Path.GetFileName(file), file));\n        }\n    }\n    catch (Exception ex)\n    {\n        // Handle exceptions (e.g., permissions issues, missing directory)\n        lblError.Text = "Could not load executables: " + ex.Message;\n    }\n}