using System;\nusing System.IO;\n\npublic class FileViewer\n{\n    private FileStream _fileStream;\n\n    public FileViewer(string filePath)\n    {\n        _fileStream = new FileStream(filePath, FileMode.Open, FileAccess.Read, FileShare.Read, bufferSize: 4096, useAsync: true);\n    }\n\n    public byte ReadByte(long position)\n    {\n        if (_fileStream.Length <= position) throw new ArgumentOutOfRangeException(nameof(position));\n\n        _fileStream.Seek(position, SeekOrigin.Begin);\n        return (byte)_fileStream.ReadByte();\n    }\n\n    public void Dispose()\n    {\n        _fileStream?.Dispose();\n    }\n}