using System;\n\npublic class DateConverter {\n    public static long ConvertMssqlUtcDateToUnixTimestamp(DateTime mssqlUtcDateTime) {\n        // Define the epoch start date for Unix timestamp\n        DateTime epochStart = new DateTime(1970, 1, 1, 0, 0, 0, DateTimeKind.Utc);\n        \n        // Calculate the difference in seconds between the MSSQL UTC date and the epoch start date\n        long unixTimestamp = (long)(mssqlUtcDateTime - epochStart).TotalSeconds;\n        \n        return unixTimestamp;\n    }\n\n    public static void Main() {\n        // Example: GetUTCDate() from MSSQL as a DateTime object\n        DateTime mssqlUtcDateTime = DateTime.UtcNow; // Simulate MSSQL's GetUTCDate()\n        \n        // Convert to Unix timestamp\n        long unixTimestamp = ConvertMssqlUtcDateToUnixTimestamp(mssqlUtcDateTime);\n        \n        Console.WriteLine("Unix Timestamp: " + unixTimestamp);\n    }\n}