using System;\nusing System.Text;\nusing log4net;\nusing Oracle.ManagedDataAccess.Client;\n\npublic class DatabaseLogger\n{\n    private static readonly ILog log = LogManager.GetLogger(typeof(DatabaseLogger));\n\n    public void ExecuteQuery(string connectionString, string statement, int id, string name, DateTime birthdate)\n    {\n        using (OracleConnection connection = new OracleConnection(connectionString))\n        using (OracleCommand command = new OracleCommand(statement, connection))\n        {\n            command.Parameters.Add(new OracleParameter(":id", id));\n            command.Parameters.Add(new OracleParameter(":name", name));\n            command.Parameters.Add(new OracleParameter(":birthdate", birthdate));\n\n            StringBuilder logQuery = new StringBuilder(command.CommandText);\n\n            foreach (OracleParameter parameter in command.Parameters)\n            {\n                string value;\n\n                // Check for date and format accordingly\n                if (parameter.Value is DateTime)\n                {\n                    value = $"TO_DATE('{((DateTime)parameter.Value):yyyy/MM/dd}', 'yyyy/mm/dd')";\n                }\n                else\n                {\n                    value = $"'{parameter.Value}'";\n                }\n\n                logQuery.Replace(parameter.ParameterName, value);\n            }\n\n            command.Connection.Open();\n            log.DebugFormat("Executing statement: {0}.", logQuery.ToString());\n            command.ExecuteNonQuery();\n            command.Connection.Close();\n        }\n    }\n}