using System;\nusing System.IO;\nusing ClearCase;\n\npublic class ClearCaseListContent\n{\n    public static void Main()\n    {\n        IClearCase cc = new ApplicationClass();\n        \n        // Get the VOB and View (though not directly used for listing)\n        CCVOB vob = cc.get_VOB("\\VOB-name");\n        CCView view = cc.get_View("ViewTag");\n\n        // Directory path to list\n        string directoryPath = @"\\path\to\your\view\directory";\n\n        DirectoryInfo dirInfo = new DirectoryInfo(directoryPath);\n        \n        foreach (FileSystemInfo fileSystemInfo in dirInfo.GetFileSystemInfos())\n        {\n            try\n            {\n                CCVersion ver = cc.get_Version(fileSystemInfo.FullName);\n                Console.WriteLine($"{fileSystemInfo.Name} - Versioned: {ver.Path}");\n            }\n            catch\n            {\n                // The file or directory is not versioned\n                Console.WriteLine($"{fileSystemInfo.Name} - Not versioned");\n            }\n        }\n    }\n}