private void AddParameter(SqlCommand cmd, string paramName, object value, ParameterDirection direction = ParameterDirection.Input)\n{\n    SqlParameter param = new SqlParameter(paramName, value);\n    param.Direction = direction;\n    cmd.Parameters.Add(param);\n}\n\npublic void SaveItem(YourItemType item)\n{\n    string commandText = "dbo.Save";\n\n    using (SqlConnection sql = new SqlConnection(_connString.ConnectionString))\n    using (SqlCommand cmd = new SqlCommand(commandText, sql) { CommandType = CommandType.StoredProcedure })\n    {\n        AddParameter(cmd, "@identity", item.Identity);\n        AddParameter(cmd, "@name", item.Name);\n        AddParameter(cmd, "@desc", item.Description);\n        AddParameter(cmd, "@title", item.Title);\n        \n        // Add more parameters using AddParameter method\n\n        sql.Open();\n        cmd.ExecuteNonQuery();\n\n        // Handle output parameters if needed\n    }\n}