// NHibernate Example\n// Requires configuration and mapping setup which is more elaborate.\nusing FluentNHibernate.Cfg;\nusing FluentNHibernate.Cfg.Db;\nusing NHibernate;\n\npublic class NHibernateHelper\n{\n    private static readonly SessionFactory = Fluently.Configure()\n        .Database(MsSqlConfiguration.MsSql2012.ConnectionString(c => c\n            .FromAppSetting("MyConnectionString")))\n        .Mappings(m => m.FluentMappings.Add(typeof(BlogMap)))\n        .BuildSessionFactory();\n\n    public ISession OpenSession()\n    {\n        return SessionFactory.OpenSession();\n    }\n}\n\npublic class BlogMap : ClassMap<Blog>\n{\n    public BlogMap()\n    {\n        Id(x => x.Id);\n        Map(x => x.Name).Column("Name");\n        // More mappings can be set up here\n    }\n}