using System;\nusing System.Windows.Forms;\nusing mshtml;\n\npublic void ExtractData()\n{\n    HtmlDocument doc = webBrowser1.Document;\n    HtmlElementCollection allDivs = doc.GetElementsByTagName("div");\n\n    foreach (HtmlElement div in allDivs)\n    {\n        if (div.GetAttribute("className") == "XYZ")\n        {\n            HtmlElementCollection childElements = div.All;\n\n            foreach (HtmlElement element in childElements)\n            {\n                if (element.TagName.ToLower() == "abc" || element.GetAttribute("class") == "ABC")\n                {\n                    // Assuming 'a' tag is a direct child or somewhere within\n                    FindLinksRecursively(element);\n                }\n            }\n        }\n    }\n}\n\nprivate void FindLinksRecursively(HtmlElement currentElement)\n{\n    if (currentElement.TagName.ToLower() == "a" && !string.IsNullOrEmpty(currentElement.GetAttribute("href")))\n    {\n        string href = currentElement.GetAttribute("href");\n        // Do something with the link, like printing it out or storing in a list\n        Console.WriteLine(href);\n        string otherInfo = currentElement.InnerText; // Example of grabbing additional info\n        Console.WriteLine(otherInfo);\n    }\n\n    foreach (HtmlElement child in currentElement.All)\n    {\n        FindLinksRecursively(child); // Recursion to traverse deeper into the structure\n    }\n}