using System.Runtime.InteropServices;\n\npublic void ReleaseComObject(object comObject)\n{\n    if (comObject != null)\n    {\n        Marshal.ReleaseComObject(comObject);\n        // Optionally set the variable to null after releasing\n        comObject = null;\n    }\n}\n\n// Example usage within your Outlook Addin code\nApplication outlookApp = new Application();\nNameSpace mapiNamespace = outlookApp.GetNamespace("MAPI");\nFolder inbox = mapiNamespace.GetDefaultFolder(OlDefaultFolders.olFolderInbox);\n\n// Do something with the COM objects\n\nReleaseComObject(inbox);\nReleaseComObject(mapiNamespace);\nReleaseComObject(outlookApp);