using System;\nusing System.Diagnostics;\nusing System.IO;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string tempFilePath = Path.Combine(Path.GetTempPath(), "tempfile.docx");\n\n        // Create the file with DeleteOnClose option and specify sharing permissions\n        using (FileStream fs = new FileStream(tempFilePath, FileMode.CreateNew, FileAccess.Write, FileShare.ReadWrite | FileShare.Delete, 4096, true, FileOptions.DeleteOnClose))\n        {\n            // Write data to the file if needed\n            using (StreamWriter sw = new StreamWriter(fs))\n            {\n                sw.WriteLine("Sample data in temporary file.");\n            }\n\n            // Launch associated application for the temp file\n            Process.Start(new ProcessStartInfo(tempFilePath) { UseShellExecute = true });\n            \n            // Keep the main process handle open until you want to allow the deletion of the file.\n            Console.WriteLine("Press any key to close file and mark it for deletion...");\n            Console.ReadKey();\n        }\n\n        // Once the FileStream is disposed, the file is marked for deletion when no other processes hold a handle.\n    }\n}