using System;\nusing System.Data.SQLite;\n\nclass Program\n{\n    static void Main()\n    {\n        string dbPath = "MyMusicLibrary.db";\n        using (var conn = new SQLiteConnection($"Data Source={dbPath};Version=3;"))\n        {\n            conn.Open();\n            \n            // Create table if it doesn't exist\n            using (var cmd = new SQLiteCommand(\n                "CREATE TABLE IF NOT EXISTS MusicFiles (" +\n                "ID INTEGER PRIMARY KEY AUTOINCREMENT, " +\n                "FilePath TEXT, " +\n                "Tags TEXT)", conn))\n            {\n                cmd.ExecuteNonQuery();\n            }\n\n            // Insert a new music file and tags\n            using (var cmd = new SQLiteCommand(\n                "INSERT INTO MusicFiles (FilePath, Tags) VALUES (@file, @tags)", conn))\n            {\n                cmd.Parameters.AddWithValue("@file", @"C:\Music\song.mp3");\n                cmd.Parameters.AddWithValue("@tags", "Rock, Happy");\n                cmd.ExecuteNonQuery();\n            }\n\n            // Query the database\n            using (var cmd = new SQLiteCommand("SELECT * FROM MusicFiles", conn))\n            using (var reader = cmd.ExecuteReader())\n            {\n                while (reader.Read())\n                {\n                    Console.WriteLine($"File: {reader["FilePath"]}, Tags: {reader["Tags"]}");\n                }\n            }\n\n        }\n    }\n}