using System;\n   using System.Data.SQLite;\n\n   namespace SQLiteExample\n   {\n       class Program\n       {\n           private const string ConnectionString = "Data Source=mydatabase.db;Version=3;";\n\n           static void Main(string[] args)\n           {\n               CreateDatabase();\n\n               InsertExample();\n               SelectExample();\n           }\n\n           private static void CreateDatabase()\n           {\n               using (var connection = new SQLiteConnection(ConnectionString))\n               {\n                   connection.Open();\n                   var command = new SQLiteCommand("CREATE TABLE IF NOT EXISTS Example(Id INTEGER PRIMARY KEY, Name TEXT)", connection);\n                   command.ExecuteNonQuery();\n               }\n           }\n\n           private static void InsertExample()\n           {\n               using (var connection = new SQLiteConnection(ConnectionString))\n               {\n                   connection.Open();\n                   var command = new SQLiteCommand("INSERT INTO Example(Name) VALUES('SampleName')", connection);\n                   command.ExecuteNonQuery();\n               }\n           }\n\n           private static void SelectExample()\n           {\n               using (var connection = new SQLiteConnection(ConnectionString))\n               {\n                   connection.Open();\n                   var command = new SQLiteCommand("SELECT * FROM Example", connection);\n                   using (SQLiteDataReader reader = command.ExecuteReader())\n                   {\n                       while (reader.Read())\n                       {\n                           Console.WriteLine($"Id: {reader["Id"]}, Name: {reader["Name"]}");\n                       }\n                   }\n               }\n           }\n       }\n   }