using Microsoft.Practices.EnterpriseLibrary.Validation;\nusing Microsoft.Practices.EnterpriseLibrary.Validation.Validators;\n\npublic class User\n{\n    [StringLengthValidator(1, 256, \n        ResourceName = "Messages", // Name of the .resx file without culture and extension\n        MessageTemplateResourceName = "EmailRequired")] // Key in the resource file\n    public string Email { get; set; }\n\n    [NotNullValidator(\n        ResourceName = "Messages",\n        MessageTemplateResourceName = "UserNameRequired")]\n    public string UserName { get; set; }\n}