protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Create an instance of the Data Access Layer class\n        DataAccessLayer dal = new DataAccessLayer();\n\n        // Get data from your method in the DAL\n        DataTable dataTable = dal.GetDataForReport();\n        \n        // Create a ReportDataSource using the data table\n        ReportDataSource reportDataSource = new ReportDataSource("DataSetName", dataTable);\n\n        // Assuming 'reportViewer' is the ID of your ReportViewer control on the ASPX page\n        reportViewer.LocalReport.DataSources.Clear();\n        reportViewer.LocalReport.DataSources.Add(reportDataSource);\n        \n        // Set the report path if not set in design view\n        reportViewer.LocalReport.ReportPath = "YourReportFileName.rdlc";\n        \n        // Refresh the report\n        reportViewer.LocalReport.Refresh();\n    }\n}