// Fetch distinct items based on tags where tag text matches\nvar itemIds = (from t in db.Tags\n               where t.Text == text\n               select t.Item.Id).Distinct();\n\n// Then fetch the items with their tags included, ordering by CreatedDate\nvar items = from i in db.Items.Include(i => i.Tags)\n            where itemIds.Contains(i.Id)\n            orderby i.CreatedDate descending\n            select i;