using Org.BouncyCastle.Crypto;\nusing Org.BouncyCastle.Crypto.Generators;\nusing Org.BouncyCastle.Crypto.Parameters;\n\npublic class BC_AESEncryption\n{\n    public static byte[] EncryptUsingBouncyCastle(byte[] inputBuffer, byte[] keyBytes)\n    {\n        KeyGenerationParameters keyGenerationParams = new KeyGenerationParameters(new SecureRandom(), 256);\n        KeyParameter keyParam = ParameterUtilities.CreateKeyParameter("AES", keyBytes);\n\n        IBufferedCipher cipher = CipherUtilities.GetCipher("AES/ECB/PKCS7Padding");\n        cipher.Init(true, keyParam);\n\n        return cipher.DoFinal(inputBuffer);\n    }\n}