protected void Button1_Click(object sender, EventArgs e)\n{\n    if (string.IsNullOrEmpty(TextBox1.Text))\n    {\n        Label3.Visible = true;\n        Label3.Text = "* Required Field";\n    }\n    else if (string.IsNullOrEmpty(TextBox2.Text))\n    {\n        Label4.Visible = true;\n        Label4.Text = "* Required Field";\n    }\n    else\n    {\n        Label3.Visible = false;\n        Label4.Visible = false;\n\n        string userid = TextBox1.Text;\n        string pass = TextBox2.Text; // Consider hashing passwords before storing them\n\n        using (SqlConnection conn = new SqlConnection("SERVER= server3\\OFFICESERVERS; Initial catalog = Web; Integrated Security = SSPI"))\n        {\n            using (SqlCommand mycmd = new SqlCommand())\n            {\n                mycmd.Connection = conn;\n                mycmd.CommandText = "SELECT COUNT(*) FROM web WHERE IsActive=1 AND LoginName=@LoginName AND Password=@Password";\n                mycmd.Parameters.AddWithValue("@LoginName", userid);\n                mycmd.Parameters.AddWithValue("@Password", pass);\n\n                try\n                {\n                    conn.Open();\n                    int count = (int)mycmd.ExecuteScalar();\n\n                    if (count == 1)\n                    {\n                        // Redirect to home page after successful login\n                        Response.Redirect("~/HomePage.aspx");\n                    }\n                    else\n                    {\n                        Label5.Visible = true;\n                        Label5.Text = "Invalid username or password";\n                    }\n                }\n                catch (Exception ex)\n                {\n                    // Handle exception appropriately (e.g., log error)\n                    Label6.Visible = true;\n                    Label6.Text = "An error occurred while processing your request.";\n                }\n            }\n        }\n\n        TextBox1.Text = string.Empty;\n        TextBox2.Text = string.Empty;\n    }\n}