// Wrapper.cs (In Wrapper project)\nusing ClassLibraryNamespace; // Assuming this is the namespace of your class library\n\nnamespace WrapperNamespace\n{\n    public class Wrapper\n    {\n        private readonly ClassLib _internalInstance;\n\n        public Wrapper(int param1, string param2) // Pass through parameters to ClassLib constructor\n        {\n            _internalInstance = new ClassLib(param1, param2);\n        }\n\n        public void Send()\n        {\n            // Add your additional functionality here\n            _internalInstance.SomeMethod(); // Example method call on the internal instance\n        }\n    }\n}\n\n// Program.cs (In another project)\nusing WrapperNamespace;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        var wrapper = new Wrapper(1, "test"); \n        wrapper.Send();\n    }\n}