using System;\nusing System.Threading;\nusing System.Web;\n\npublic class StreamingExample : IHttpHandler\n{\n    public void ProcessRequest(HttpContext context)\n    {\n        // Disable buffering\n        HttpContext.Current.Response.Buffer = false;\n        HttpContext.Current.Response.BufferOutput = false;\n\n        for (int i = 0; i < 10; i++)\n        {\n            // Simulate some processing happening\n            Thread.Sleep(500); \n\n            HttpContext.Current.Response.Write(string.Format("Processed {0} items<br>", i));\n            // Flush the response to the client immediately\n            HttpContext.Current.Response.Flush();\n        }\n\n        // Ensure all content is sent and close the response\n        HttpContext.Current.Response.Close();\n    }\n\n    public bool IsReusable => false;\n}