public class AccountController : Controller {\n    private readonly IAuthenticationService _authenticationService;\n\n    public AccountController(IAuthenticationService authService) {\n        _authenticationService = authService;\n    }\n\n    [HttpPost]\n    public ActionResult Login(LoginViewModel model) {\n        if (!ModelState.IsValid)\n            return View(model);\n\n        var isAuthenticated = _authenticationService.Authenticate(model.Username, model.Password);\n        if (isAuthenticated) {\n            // Handle login success\n        } else {\n            ModelState.AddModelError("", "Invalid username or password");\n        }\n\n        return View(model);\n    }\n\n    [HttpPost]\n    public ActionResult Register(RegisterViewModel model) {\n        if (!ModelState.IsValid)\n            return View(model);\n\n        var user = new User { Username = model.Username, Password = model.Password, Email = model.Email };\n        \n        // Save the user to your data store and handle registration success\n        _authenticationService.Register(user);\n        \n        return RedirectToAction("Index", "Home");\n    }\n}