using System;\n\npublic class DateConverter\n{\n    public static DateTime ConvertToLocalTime(DateTime utcDateTime)\n    {\n        TimeZoneInfo localZone = TimeZoneInfo.Local;\n        return TimeZoneInfo.ConvertTimeFromUtc(utcDateTime, localZone);\n    }\n    \n    public static DateTime ConvertToUTC(DateTime localDateTime)\n    {\n        TimeZoneInfo localZone = TimeZoneInfo.Local;\n        return TimeZoneInfo.ConvertTimeToUtc(localDateTime, localZone);\n    }\n}\n\n// Usage example\npublic class DataRetrievalService\n{\n    public DataSet GetDataSet()\n    {\n        // Assume ds is your dataset with a DateTime column named 'DateColumn'\n        var ds = RetrieveFromWebService(); \n\n        foreach (DataRow row in ds.Tables[0].Rows)\n        {\n            // Convert UTC date to local time\n            var utcDateTime = (DateTime)row["DateColumn"];\n            var localDateTime = DateConverter.ConvertToLocalTime(utcDateTime);\n            row["DateColumn"] = localDateTime;\n        }\n\n        return ds;\n    }\n\n    private DataSet RetrieveFromWebService()\n    {\n        // Simulation of data retrieval from web service\n        DataSet ds = new DataSet();\n        DataTable dt = new DataTable();\n        DataColumn dateCol = new DataColumn("DateColumn", typeof(DateTime));\n        dt.Columns.Add(dateCol);\n\n        DataRow row = dt.NewRow();\n        row["DateColumn"] = TimeZoneInfo.ConvertTimeToUtc(new DateTime(2007, 1, 14)); // Storing UTC\n        dt.Rows.Add(row);\n        \n        ds.Tables.Add(dt);\n        return ds;\n    }\n}