using System;\nusing System.Linq;\nusing System.ServiceProcess;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string[] computers = { "localhost", "example.com" }; // Replace with your list of computer names\n\n        CheckRemoteDesktopServiceStatus(computers);\n    }\n\n    static void CheckRemoteDesktopServiceStatus(string[] machines)\n    {\n        const string serviceName = "TermService";\n\n        foreach (var machine in machines)\n        {\n            try\n            {\n                using (var sc = new ServiceController(serviceName, machine))\n                {\n                    Console.WriteLine($"Checking on {machine}...");\n                    var status = sc.Status;\n                    \n                    if (status == ServiceControllerStatus.Running)\n                    {\n                        Console.WriteLine($"Remote Desktop Services is running on {machine}.");\n                    }\n                    else\n                    {\n                        Console.WriteLine($"Remote Desktop Services is NOT running on {machine}. Current status: {status}");\n                    }\n                }\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"Failed to check Remote Desktop Services on {machine}: {ex.Message}");\n            }\n        }\n    }\n}