using System.Collections.Generic;\n   using Newtonsoft.Json;\n\n   public class DataRowModel\n   {\n       public string Column1 { get; set; }\n       public int Column2 { get; set; }\n       // Add other columns as necessary\n   }\n\n   public class DataProcessor\n   {\n       // Convert a list of data to JSON before sending it\n       public string ConvertToJson(IEnumerable<DataRowModel> rows)\n       {\n           return JsonConvert.SerializeObject(rows);\n       }\n\n       // Convert received JSON back to data model after receiving at the web service\n       public IEnumerable<DataRowModel> ConvertFromJson(string json)\n       {\n           return JsonConvert.DeserializeObject<IEnumerable<DataRowModel>>(json);\n       }\n   }